import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Grid,
  Paper,
  Typography,
  Button,
  CircularProgress,
  Backdrop,
} from '@mui/material';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from 'recharts';
import SecurityIcon from '@mui/icons-material/Security';
import WarningIcon from '@mui/icons-material/Warning';
import ErrorAlert from './ErrorAlert';
import { handleApiError } from '../utils/errorHandler';
import api from '../services/api';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [scanning, setScanning] = useState(false);
  const [error, setError] = useState(null);
  const [scanResults, setScanResults] = useState(null);
  const [resources, setResources] = useState(null);

  const startScan = async () => {
    try {
      setScanning(true);
      setError(null);
      const response = await api.post('/scan');
      setScanResults(response.data.data);
    } catch (err) {
      const errorData = handleApiError(err);
      setError(errorData);
    } finally {
      setScanning(false);
    }
  };

  useEffect(() => {
    const fetchResources = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await api.get('/resources');
        setResources(response.data.data);
      } catch (err) {
        const errorData = handleApiError(err);
        setError(errorData);
      } finally {
        setLoading(false);
      }
    };

    fetchResources();
  }, []);

  const vulnerabilityData = scanResults?.vulnerabilities ? [
    {
      name: 'Vulnerabilities',
      Critical: scanResults.vulnerabilities.CRITICAL,
      High: scanResults.vulnerabilities.HIGH,
      Medium: scanResults.vulnerabilities.MEDIUM,
      Low: scanResults.vulnerabilities.LOW,
    },
  ] : [];

  if (loading) {
    return (
      <Backdrop open={true} sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}>
        <CircularProgress color="inherit" />
      </Backdrop>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        {/* Error Alert */}
        {error && (
          <Grid item xs={12}>
            <ErrorAlert error={error} onClose={() => setError(null)} />
          </Grid>
        )}

        {/* Header */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="h4" component="h1">
              Kubernetes Vulnerability Scanner
            </Typography>
            <Button
              variant="contained"
              color="primary"
              onClick={startScan}
              disabled={scanning}
              startIcon={scanning ? <CircularProgress size={20} /> : <SecurityIcon />}
            >
              {scanning ? 'Scanning...' : 'Start Scan'}
            </Button>
          </Paper>
        </Grid>

        {/* Resource Overview */}
        {resources && (
          <>
            <Grid item xs={12} md={4}>
              <Paper sx={{ p: 2, textAlign: 'center' }}>
                <Typography variant="h6">Pods</Typography>
                <Typography variant="h3">{resources.pods}</Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper sx={{ p: 2, textAlign: 'center' }}>
                <Typography variant="h6">Services</Typography>
                <Typography variant="h3">{resources.services}</Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper sx={{ p: 2, textAlign: 'center' }}>
                <Typography variant="h6">Nodes</Typography>
                <Typography variant="h3">{resources.nodes}</Typography>
              </Paper>
            </Grid>
          </>
        )}

        {/* Vulnerability Chart */}
        {scanResults && (
          <Grid item xs={12}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Vulnerability Overview
              </Typography>
              <Box sx={{ width: '100%', height: 300 }}>
                <BarChart
                  data={vulnerabilityData}
                  margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="Critical" fill="#ff1744" />
                  <Bar dataKey="High" fill="#f50057" />
                  <Bar dataKey="Medium" fill="#ff9800" />
                  <Bar dataKey="Low" fill="#4caf50" />
                </BarChart>
              </Box>
            </Paper>
          </Grid>
        )}

        {/* Detailed Results */}
        {scanResults && (
          <Grid item xs={12}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Scan Details
              </Typography>
              {scanResults.pods.map((pod, index) => (
                <Box key={index} sx={{ mb: 2 }}>
                  <Typography variant="subtitle1">
                    Pod: {pod.name} ({pod.namespace})
                  </Typography>
                  {pod.vulnerabilities.map((vuln, vIndex) => (
                    <Box
                      key={vIndex}
                      sx={{
                        display: 'flex',
                        alignItems: 'center',
                        ml: 2,
                        color: vuln.severity === 'CRITICAL' ? '#ff1744' : 
                               vuln.severity === 'HIGH' ? '#f50057' :
                               vuln.severity === 'MEDIUM' ? '#ff9800' : '#4caf50'
                      }}
                    >
                      <WarningIcon sx={{ mr: 1 }} />
                      <Typography variant="body2">
                        {vuln.description} ({vuln.severity})
                      </Typography>
                    </Box>
                  ))}
                </Box>
              ))}
            </Paper>
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default Dashboard;
