import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  IconButton,
  Button,
  AppBar,
  Toolbar,
  useTheme,
  CircularProgress,
  Snackbar,
  Alert
} from '@mui/material';
import {
  Brightness4 as DarkModeIcon,
  Brightness7 as LightModeIcon,
  Security as SecurityIcon,
  Warning as WarningIcon,
  BugReport as BugIcon,
  CheckCircle as CheckCircleIcon,
  Logout as LogoutIcon
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { useColorMode } from '../theme/ThemeProvider';

const Dashboard = () => {
  const theme = useTheme();
  const { toggleColorMode } = useColorMode();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [scanResults, setScanResults] = useState(null);
  const [alert, setAlert] = useState({
    open: false,
    message: '',
    severity: 'info'
  });

  const handleScan = async () => {
    setLoading(true);
    try {
      const response = await api.post('/scan');
      if (response.data.status === 'success') {
        setScanResults(response.data.data);
        setAlert({
          open: true,
          message: 'Scan completed successfully!',
          severity: 'success'
        });
      }
    } catch (error) {
      console.error('Scan error:', error);
      setAlert({
        open: true,
        message: error.response?.data?.message || 'Failed to perform scan',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleCloseAlert = () => {
    setAlert(prev => ({ ...prev, open: false }));
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      bgcolor: 'background.default',
      color: 'text.primary'
    }}>
      <AppBar position="static" color="primary" elevation={4}>
        <Toolbar>
          <SecurityIcon sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Kubernetes Vulnerability Scanner
          </Typography>
          <IconButton onClick={toggleColorMode} color="inherit">
            {theme.palette.mode === 'dark' ? <LightModeIcon /> : <DarkModeIcon />}
          </IconButton>
          <IconButton onClick={handleLogout} color="inherit">
            <LogoutIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Grid container spacing={3}>
          {/* Scan Button */}
          <Grid item xs={12}>
            <Paper 
              sx={{ 
                p: 3, 
                display: 'flex', 
                flexDirection: 'column', 
                alignItems: 'center',
                background: theme.palette.mode === 'dark' 
                  ? 'linear-gradient(45deg, #1a237e 30%, #0d47a1 90%)'
                  : 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
                color: 'white'
              }}
            >
              <Typography variant="h5" gutterBottom>
                Kubernetes Cluster Scanner
              </Typography>
              <Button
                variant="contained"
                onClick={handleScan}
                disabled={loading}
                sx={{
                  mt: 2,
                  bgcolor: 'rgba(255, 255, 255, 0.2)',
                  '&:hover': {
                    bgcolor: 'rgba(255, 255, 255, 0.3)',
                  },
                }}
              >
                {loading ? <CircularProgress size={24} color="inherit" /> : 'Start Scan'}
              </Button>
            </Paper>
          </Grid>

          {/* Scan Results */}
          {scanResults && (
            <>
              {/* Vulnerabilities Summary */}
              <Grid item xs={12} md={4}>
                <Card>
                  <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <WarningIcon color="error" sx={{ mr: 1 }} />
                      <Typography variant="h6">
                        Vulnerabilities
                      </Typography>
                    </Box>
                    <Typography variant="h4">
                      {scanResults.vulnerabilities?.length || 0}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>

              {/* Pods Summary */}
              <Grid item xs={12} md={4}>
                <Card>
                  <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <BugIcon color="warning" sx={{ mr: 1 }} />
                      <Typography variant="h6">
                        Pods Scanned
                      </Typography>
                    </Box>
                    <Typography variant="h4">
                      {scanResults.pods?.length || 0}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>

              {/* CVEs Summary */}
              <Grid item xs={12} md={4}>
                <Card>
                  <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <CheckCircleIcon color="success" sx={{ mr: 1 }} />
                      <Typography variant="h6">
                        CVEs Found
                      </Typography>
                    </Box>
                    <Typography variant="h4">
                      {scanResults.cves?.length || 0}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>

              {/* Detailed Results */}
              <Grid item xs={12}>
                <Card>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Scan Summary
                    </Typography>
                    <pre style={{ 
                      overflow: 'auto', 
                      backgroundColor: theme.palette.mode === 'dark' ? '#2d2d2d' : '#f5f5f5',
                      padding: '1rem',
                      borderRadius: '4px',
                      color: theme.palette.text.primary
                    }}>
                      {JSON.stringify(scanResults.summary, null, 2)}
                    </pre>
                  </CardContent>
                </Card>
              </Grid>
            </>
          )}
        </Grid>
      </Container>

      <Snackbar
        open={alert.open}
        autoHideDuration={6000}
        onClose={handleCloseAlert}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert
          onClose={handleCloseAlert}
          severity={alert.severity}
          sx={{ width: '100%' }}
        >
          {alert.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Dashboard;
